mapper.select:
select count(*) from tbl_activity a join tbl_user u on a.owner=u.id
a.name like '%' #{name} '%'
and u.name like '%' #{owner} '%'
and a.startDate > #{startDate}
and a.endDate < #{endDate}

select a.id, a.name, u.name as owner, a.startDate, a.endDate from tbl_activity a join tbl_user u on a.owner=u.id
a.name like "%" #{name} "%"
and u.name like "%" #{owner} "%"
and a.startDate > #{startDate}
and a.endDate < #{endDate}
order by a.createTime desc limit #{skipCount},#{pageSize}

mapper.delete:
delete from tbl_activity
where id in

    #{id}

mapper.select:
select
car.id as id, a.name, a.startDate, a.endDate, u.name as owner
from tbl_activity a join tbl_user u on a.owner = u.id join tbl_clue_activity_relation car on a.id = car.activityId where car.clueId = #{clueId}
